// Experience data converted from the original index.js
export const experienceData = {
  "experience1": {
    "title": "Senior Full Stack Developer",
    "company": "TechCorp Inc.",
    "period": "2022 - Present",
    "description": "Leading development of enterprise-level web applications using React, Node.js, and cloud technologies. Mentoring junior developers and implementing best practices for scalable software architecture.",
    "achievements": [
      "Led a team of 5 developers in building a microservices architecture",
      "Improved application performance by 60% through optimization",
      "Implemented CI/CD pipelines reducing deployment time by 80%",
      "Mentored 3 junior developers who were promoted to mid-level"
    ],
    "technologies": ["React", "Node.js", "AWS", "Docker", "Kubernetes", "PostgreSQL"]
  },
  "experience2": {
    "title": "Full Stack Developer",
    "company": "Digital Solutions Ltd.",
    "period": "2020 - 2022",
    "description": "Developed full-stack web applications and mobile apps for various clients. Collaborated with design teams to create user-friendly interfaces and implemented robust backend systems.",
    "achievements": [
      "Built 15+ client projects with 100% on-time delivery",
      "Developed a real-time chat application serving 10,000+ users",
      "Created reusable component library used across 5 projects",
      "Reduced bug reports by 40% through improved testing practices"
    ],
    "technologies": ["React", "Vue.js", "Express.js", "MongoDB", "Socket.io", "Jest"]
  },
  "experience3": {
    "title": "Frontend Developer",
    "company": "WebCraft Studios",
    "period": "2019 - 2020",
    "description": "Specialized in creating responsive and interactive user interfaces. Worked closely with UX/UI designers to implement pixel-perfect designs and optimize user experience.",
    "achievements": [
      "Converted 20+ PSD designs to responsive web applications",
      "Improved page load speeds by 50% through optimization",
      "Implemented accessibility features meeting WCAG 2.1 standards",
      "Created animation library used across company projects"
    ],
    "technologies": ["HTML5", "CSS3", "JavaScript", "Sass", "Webpack", "GSAP"]
  },
  "experience4": {
    "title": "Junior Web Developer",
    "company": "StartupHub",
    "period": "2018 - 2019",
    "description": "Started my professional journey building websites and web applications. Learned modern development practices and contributed to various startup projects.",
    "achievements": [
      "Built first commercial website from scratch",
      "Learned version control and collaborative development",
      "Contributed to open-source projects",
      "Completed 50+ online courses and certifications"
    ],
    "technologies": ["HTML", "CSS", "JavaScript", "jQuery", "Bootstrap", "Git"]
  }
};

export default experienceData;
