import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { projectData } from '../data/projectData';

const Home = () => {
  const [activeTab, setActiveTab] = useState('all');
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    // Load projects based on active tab
    const loadProjects = () => {
      const projectArray = Object.values(projectData);
      let filteredProjects = projectArray;

      if (activeTab === 'live') {
        filteredProjects = projectArray.filter(project => project.category === 'live');
      } else if (activeTab === 'figma') {
        filteredProjects = projectArray.filter(project => project.category === 'figma');
      }

      // Take first 4 projects for home page
      setProjects(filteredProjects.slice(0, 4));
    };

    loadProjects();
  }, [activeTab]);

  const generateTechTags = (project) => {
    const techs = ['React', 'Node.js', 'MongoDB', 'AWS', 'Docker'];
    return techs.slice(0, Math.floor(Math.random() * 3) + 2);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <section className="relative bg-gradient-to-br from-purple-600 via-blue-600 to-indigo-700 text-white py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              Hi, I'm <span className="text-yellow-300">John Doe</span>
            </h1>
            <p className="text-xl md:text-2xl mb-8 text-gray-200">
              Full Stack Developer & UI/UX Designer
            </p>
            <p className="text-lg mb-12 max-w-3xl mx-auto text-gray-300">
              I create beautiful, functional, and user-centered digital experiences 
              that solve real-world problems and drive business growth.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link 
                to="/projects" 
                className="btn-mimas btn-mimas--white"
              >
                <span>View My Work</span>
              </Link>
              <Link 
                to="/contact" 
                className="btn-mimas"
              >
                <span>Get In Touch</span>
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Skills Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              My Skills
            </h2>
            <p className="text-lg text-gray-600 max-w-2xl mx-auto">
              I specialize in modern web technologies and frameworks to create 
              exceptional digital experiences.
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {[
              { name: 'Frontend Development', icon: 'ðŸŽ¨', skills: ['React', 'Vue.js', 'TypeScript', 'Tailwind CSS'] },
              { name: 'Backend Development', icon: 'âš™ï¸', skills: ['Node.js', 'Python', 'Express.js', 'Django'] },
              { name: 'Database & Cloud', icon: 'â˜ï¸', skills: ['MongoDB', 'PostgreSQL', 'AWS', 'Docker'] },
              { name: 'UI/UX Design', icon: 'ðŸŽ¯', skills: ['Figma', 'Adobe XD', 'Sketch', 'Prototyping'] }
            ].map((skill, index) => (
              <div key={index} className="text-center p-6 rounded-lg bg-gray-50 hover:bg-gray-100 transition-colors duration-200">
                <div className="text-4xl mb-4">{skill.icon}</div>
                <h3 className="text-xl font-semibold text-gray-900 mb-3">{skill.name}</h3>
                <div className="flex flex-wrap justify-center gap-2">
                  {skill.skills.map((tech, techIndex) => (
                    <span 
                      key={techIndex}
                      className="px-3 py-1 bg-purple-100 text-purple-800 text-sm rounded-full"
                    >
                      {tech}
                    </span>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Projects Section */}
      <section className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              Featured Projects
            </h2>
            <p className="text-lg text-gray-600 max-w-2xl mx-auto mb-8">
              Here are some of my recent projects that showcase my skills and expertise.
            </p>

            {/* Project Tabs */}
            <div className="flex justify-center mb-12">
              <div className="bg-gray-800 rounded-lg p-1 flex">
                {[
                  { id: 'all', label: 'All Projects', count: Object.keys(projectData).length },
                  { id: 'live', label: 'Live Projects', count: Object.values(projectData).filter(p => p.category === 'live').length },
                  { id: 'figma', label: 'Figma Converters', count: Object.values(projectData).filter(p => p.category === 'figma').length }
                ].map((tab) => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`tab-button ${activeTab === tab.id ? 'active' : ''}`}
                  >
                    {tab.label}
                    <span>{tab.count}</span>
                  </button>
                ))}
              </div>
            </div>
          </div>

          {/* Projects Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {projects.map((project, index) => (
              <div key={index} className="project-card card">
                <div className="mb-4">
                  <img 
                    src={project.image} 
                    alt={project.title}
                    className="w-full h-48 object-cover rounded-lg"
                  />
                </div>
                <div>
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">
                    {project.title}
                  </h3>
                  <p className="text-gray-600 mb-4 line-clamp-2">
                    {project.description}
                  </p>
                  <div className="flex flex-wrap gap-2 mb-4">
                    {generateTechTags(project).map((tech, techIndex) => (
                      <span 
                        key={techIndex}
                        className="px-2 py-1 bg-gray-100 text-gray-700 text-xs rounded"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-500">{project.year}</span>
                    <Link 
                      to={`/project/${Object.keys(projectData).find(key => projectData[key] === project)}`}
                      className="text-purple-600 hover:text-purple-700 font-medium"
                    >
                      View Details â†’
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="text-center mt-12">
            <Link 
              to="/projects" 
              className="btn-mimas"
            >
              <span>View All Projects</span>
            </Link>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-gradient-to-r from-purple-600 to-blue-600 text-white">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-3xl md:text-4xl font-bold mb-6">
            Ready to Start Your Project?
          </h2>
          <p className="text-xl mb-8 text-gray-200">
            Let's work together to bring your ideas to life with cutting-edge technology 
            and exceptional design.
          </p>
          <Link 
            to="/contact" 
            className="btn-mimas btn-mimas--white"
          >
            <span>Get In Touch</span>
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
